import React, { useState } from 'react';
import problems from './problems.json';  // Ensure you have this JSON file with problem data
import Banner from './Banner';
import Categories from './Categories';

function App() {
  const [topics, setTopics] = useState([
    { id: 'cpp', name: 'Programming in C++', rank: 0 },
    { id: 'adt', name: 'ADTs', rank: 0 },
    { id: 'recursion', name: 'Recursion', rank: 0 },
    { id: 'backtracking', name: 'Backtracking', rank: 0 },
    { id: 'classes', name: 'Classes', rank: 0 },
    { id: 'linkedLists', name: 'Linked Lists', rank: 0 },
    { id: 'trees', name: 'Trees', rank: 0 }
  ]);
  const [recommendedProblems, setRecommendedProblems] = useState([]);

  const handleRankChange = (newTopics) => {
    if (Array.isArray(newTopics)) {
      setTopics(newTopics.map((topic, index) => ({ ...topic, rank: index + 1 }))); // Auto-update rank based on array order
    }
  };

  const handleSubmit = () => {
    const sortedTopics = [...topics].sort((a, b) => a.rank - b.rank).slice(0, 3);
    fetchProblems(sortedTopics.map(topic => topic.name));
    console.log("Current Rankings:", topics);
    console.log("Top Three Topics:", sortedTopics);
    alert('Rankings submitted!');
  };

  const fetchProblems = (topThreeTopics) => {
    const filteredProblems = problems.filter(problem =>
      topThreeTopics.includes(problem.topic)
    );
    setRecommendedProblems(filteredProblems);
    console.log("Recommended Problems:", filteredProblems);
  };

  return (
    <div className="App">
      <Banner />
      <Categories topics={topics} onRankChange={handleRankChange} onSubmit={handleSubmit} />
      {recommendedProblems.length > 0 && (
        <div>
          <h2>Recommended Problems:</h2>
          <ul>
            {recommendedProblems.map((problem, index) => (
              <li key={index}>{problem.name}</li>
            ))}
          </ul>
        </div>
      )}
    </div>
  );
}

export default App;
