import React, { useState } from 'react';
import problems from './problems.json';
import Categories from './Categories';
import './Banner.css';

function App() {
  const [topics, setTopics] = useState([
    { id: 'cpp', name: 'Programming in C++ and Big-O', rank: 1 },
    { id: 'adt', name: 'ADTs', rank: 2 },
    { id: 'recursion', name: 'Recursion', rank: 3 },
    { id: 'backtracking', name: 'Backtracking', rank: 4 },
    { id: 'classes', name: 'Classes', rank: 5 },
    { id: 'linkedLists', name: 'Linked Lists', rank: 6 },
    { id: 'trees', name: 'Trees', rank: 7 }
  ]);
  const [recommendedProblems, setRecommendedProblems] = useState([]);
  const [completedProblems, setCompletedProblems] = useState([]);

  const handleRankChange = (newTopics) => {
    if (Array.isArray(newTopics)) {
      setTopics(newTopics.map((topic, index) => ({ ...topic, rank: index + 1 })));
    }
  };

  const handleSubmit = () => {
    const sortedTopics = [...topics].sort((a, b) => a.rank - b.rank).slice(0, 3);
    fetchProblems(sortedTopics);
    console.log("Current Rankings:", topics);
    console.log("Top Three Topics:", sortedTopics);
    alert('Rankings submitted!');
  };

  const fetchProblems = (topThreeTopics) => {
    let enrichedProblems = problems
      .filter(problem => topThreeTopics.some(topic => topic.name === problem.topic) && !completedProblems.includes(problem.name))
      .map(problem => ({
        ...problem,
        updatedProbability: computePosteriorProbabilities(problem, topThreeTopics.find(topic => topic.name === problem.topic).rank)
      }));

    const sortedProblems = enrichedProblems.sort((a, b) => b.updatedProbability - a.updatedProbability).slice(0, 3);
    setRecommendedProblems(sortedProblems);

    console.log("Top 3 Recommended Problems with Posterior Probabilities:", sortedProblems);
  };

  const computePosteriorProbabilities = (problem, rank) => {
    const likelihood = 1 / rank + 0.05;
    const posteriorProbability = problem.prior_probability * likelihood;
    return posteriorProbability;
  };

  const markAsCompleted = (problemName) => {
    setCompletedProblems([...completedProblems, problemName]);
  };

  return (
    <div className="App">
      <div className="banner-container">
        <div className="banner-content">
          <h1>CS106B Practice Problem Recommender</h1>
          <p>Welcome! Please rank what topics you would like to practice today:</p>
        </div>
      </div>
      <div className="main-container">
        <div className="leftColumn">
          <Categories topics={topics} onRankChange={handleRankChange} onSubmit={handleSubmit} />
        </div>
        <div className="rightColumn">
          {recommendedProblems.length > 0 && (
            <div>
              <h2>Recommended Problems:</h2>
              <ul>
                {recommendedProblems.map((problem, index) => (
                  <li key={index}>
                    <p><strong>{index + 1}) {problem.name}</strong></p>
                    <p>Topic: {problem.topic}</p>
                    <p>
                      <a href={problem.link} target="_blank" rel="noopener noreferrer">
                        Link to {problem.name}
                      </a>
                    </p>
                    <button onClick={() => markAsCompleted(problem.name)}>Mark as Completed</button>
                  </li>
                ))}
              </ul>
            </div>
          )}
        </div>
      </div>
    </div>
  );
}

export default App;
