import React from 'react';
import { DragDropContext, Droppable, Draggable } from 'react-beautiful-dnd';
import './Categories.css';

function Categories({ topics, onRankChange, onSubmit }) {
  const handleOnDragEnd = (result) => {
    if (!result.destination) return; // If the item is dropped outside the list, do nothing

    const newTopics = Array.from(topics); // Create a copy of the current topics
    const [reorderedItem] = newTopics.splice(result.source.index, 1); // Remove the item from its original position
    newTopics.splice(result.destination.index, 0, reorderedItem); // Insert the item at its new position

    onRankChange(newTopics); // Update the topics in the parent component's state
  };

  return (
    <DragDropContext onDragEnd={handleOnDragEnd}>
      <Droppable droppableId="topics">
        {(provided) => (
          <div {...provided.droppableProps} ref={provided.innerRef} className="topics-list">
            {topics.map((topic, index) => (
              <Draggable key={topic.id} draggableId={topic.id} index={index}>
                {(provided) => (
                  <div
                    ref={provided.innerRef}
                    {...provided.draggableProps}
                    {...provided.dragHandleProps}
                    className="category">
                    <h3>{topic.name}</h3>
                    <input
                      type="number"
                      value={topic.rank}
                      onChange={(e) => onRankChange(topic.id, parseInt(e.target.value, 10))}
                    />
                  </div>
                )}
              </Draggable>
            ))}
            {provided.placeholder}
          </div>
        )}
      </Droppable>
      <button onClick={onSubmit} className="submit-button">Submit</button>
    </DragDropContext>
  );
}

export default Categories;
