import React, { useState } from 'react';
import problems from './problems.json';
import Banner from './Banner';
import Categories from './Categories';
import './index.css';  // Ensure that index.css is imported
import './banner.css';  // Ensure that banner.css is imported
import './categories.css';  // Ensure that categories.css is imported

function App() {
  const [topics, setTopics] = useState([
    { id: 'cpp', name: 'Programming in C++', rank: 0 },
    { id: 'adt', name: 'ADTs', rank: 0 },
    { id: 'recursion', name: 'Recursion', rank: 0 },
    { id: 'backtracking', name: 'Backtracking', rank: 0 },
    { id: 'classes', name: 'Classes', rank: 0 },
    { id: 'linkedLists', name: 'Linked Lists', rank: 0 },
    { id: 'trees', name: 'Trees', rank: 0 }
  ]);
  const [recommendedProblem, setRecommendedProblem] = useState(null);

  const handleRankChange = (newTopics) => {
    if (Array.isArray(newTopics)) {
      setTopics(newTopics.map((topic, index) => ({ ...topic, rank: index + 1 })));
    }
  };

  const handleSubmit = () => {
    const sortedTopics = [...topics].sort((a, b) => a.rank - b.rank).slice(0, 3);
    fetchProblems(sortedTopics);
    console.log("Current Rankings:", topics);
    console.log("Top Three Topics:", sortedTopics);
    alert('Rankings submitted!');
  };

  const fetchProblems = (topThreeTopics) => {
    let enrichedProblems = problems
      .filter(problem => topThreeTopics.some(topic => topic.name === problem.topic))
      .map(problem => ({
        ...problem,
        updatedProbability: computePosteriorProbabilities(problem, topThreeTopics.find(topic => topic.name === problem.topic).rank)
      }));

    const mapProblem = findMAP(enrichedProblems);
    setRecommendedProblem(mapProblem);

    console.log("Recommended Problem with Posterior Probability:", mapProblem);
  };

  const computePosteriorProbabilities = (problem, rank) => {
    const likelihood = 1 / rank + 0.05;
    const posteriorProbability = problem.prior_probability * likelihood;
    return posteriorProbability;
  };

  const findMAP = (problems) => {
    if (problems.length === 0) return null;
    return problems.reduce((max, problem) => max.updatedProbability > problem.updatedProbability ? max : problem);
  };

  return (
    <div className="App">
      <Banner />
      <div className="main-container">
        <div className="leftColumn">
          <Categories topics={topics} onRankChange={handleRankChange} onSubmit={handleSubmit} />
        </div>
        <div className="rightColumn recommended-problems">
          {recommendedProblem && (
            <div>
              <h2>Recommended Problem:</h2>
              <p>
                <a href="https://web.stanford.edu/class/cs106b/section/extra1/" target="_blank" rel="noopener noreferrer">
                  {recommendedProblem.name}
                </a>
              </p>
            </div>
          )}
        </div>
      </div>
    </div>
  );
}

export default App;
