import React, { useState } from 'react';
import problems from './problems.json';
import Categories from './Categories';
import Modal from 'react-modal';
import './Banner.css';

Modal.setAppElement('#root');

function App() {
  const [topics, setTopics] = useState([
    { id: 'cpp', name: 'Programming in C++ and Big-O', rank: 1, dirichletCount: 1 },
    { id: 'adt', name: 'ADTs', rank: 2, dirichletCount: 1 },
    { id: 'recursion', name: 'Recursion', rank: 3, dirichletCount: 4 },
    { id: 'backtracking', name: 'Backtracking', rank: 4, dirichletCount: 6 },
    { id: 'classes', name: 'Classes', rank: 5, dirichletCount: 1 },
    { id: 'linkedLists', name: 'Linked Lists', rank: 6, dirichletCount: 2 },
    { id: 'trees', name: 'Trees', rank: 7, dirichletCount: 2 }
  ]);
  const [recommendedProblems, setRecommendedProblems] = useState([]);
  const [completedProblems, setCompletedProblems] = useState([]);
  const [modalIsOpen, setModalIsOpen] = useState(false);
  const [currentProblem, setCurrentProblem] = useState(null);
  const [feedback, setFeedback] = useState(3);  // Default feedback is 3

  const handleRankChange = (newTopics) => {
    setTopics(newTopics.map((topic, index) => ({ ...topic, rank: index + 1 })));
  };

  const handleSubmit = () => {
    const updatedTopics = topics.map(topic => ({
      ...topic,
      dirichletCount: topic.dirichletCount + (8 - topic.rank)
    }));

    // Fetch problems based on these updated counts
    recommendNewProblems();
    console.log("Updated Dirichlet counts and MAP calculations:", updatedTopics);
    alert('Rankings submitted!');
  };

  const updateDirichletCounts = () => {
    const feedbackValue = parseInt(feedback);
    setTopics(prevTopics => prevTopics.map(topic => ({
      ...topic,
      dirichletCount: topic.id === currentProblem.id ? 
        topic.dirichletCount + feedbackValue : 
        topic.dirichletCount + (5 - feedbackValue)
    })));
  };

  const fetchProblems = (updatedTopics) => {
    const m = updatedTopics.length;
    const totalDirichletCounts = updatedTopics.reduce((acc, cur) => acc + cur.dirichletCount, 0);

    // Calculate MAP for each topic and sort by it
    updatedTopics.forEach(topic => {
      topic.MAP = MAP(topic.dirichletCount, totalDirichletCounts, m);
    });
    const sortedTopics = updatedTopics.sort((a, b) => b.MAP - a.MAP).slice(0, 3);

    // Select one random problem from each of the top three topics
    let selectedProblems = sortedTopics.map(topic => {
      const topicProblems = problems.filter(problem => problem.topic === topic.name && !completedProblems.includes(problem.name));
      return topicProblems[Math.floor(Math.random() * topicProblems.length)];
    }).filter(problem => problem); // Ensure undefined values are removed in case of empty filters

    setRecommendedProblems(selectedProblems);
    console.log("Top 3 Recommended Problems based on MAP:", selectedProblems);
  };



  const MAP = (dirichletCount, totalDirichletCounts, m) => {
    return (dirichletCount - 1) / (totalDirichletCounts - m);
  };


  const markAsCompleted = (problemName, topic) => {
    setCurrentProblem({ problemName, topic });
    setModalIsOpen(true);
  };

  const handleFeedbackSubmit = () => {
    // Update all topics' Dirichlet counts based on the feedback
    const feedbackRank = parseInt(feedback);
    setTopics(prevTopics => prevTopics.map(topic => ({
      ...topic,
      dirichletCount: topic.id === currentProblem.id ? topic.dirichletCount + feedbackRank : topic.dirichletCount + (5 - feedbackRank)
    })));

    setCompletedProblems([...completedProblems, currentProblem.problemName]);
    console.log(`User feedback for ${currentProblem.topic}: ${feedback}`);
    setModalIsOpen(false);
  };

  return (
    <div className="App">
      <div className="banner-container">
        <div className="banner-content">
          <h1>CS106B Practice Problem Recommender</h1>
          <p>Welcome! Please rank your preferences on what to study:</p>
        </div>
      </div>
      <div className="main-container">
        <div className="leftColumn">
          <Categories topics={topics} onRankChange={handleRankChange} onSubmit={handleSubmit} />
        </div>
        <div className="rightColumn">
          {recommendedProblems.length > 0 && (
            <div>
              <h2>Recommended Problems:</h2>
              <ul>
                {recommendedProblems.map((problem, index) => (
                  <li key={index}>
                    <p><strong>{index + 1}) {problem.name}</strong></p>
                    <p>Topic: {problem.topic}</p>
                    <p>
                      <a href={problem.link} target="_blank" rel="noopener noreferrer">
                        Link to {problem.name}
                      </a>
                    </p>
                    <button
                      onClick={() => markAsCompleted(problem.name, problem.topic)}
                      style={{ backgroundColor: completedProblems.includes(problem.name) ? 'green' : 'initial' }}
                    >
                      Mark as Completed
                    </button>
                  </li>
                ))}
              </ul>
            </div>
          )}
        </div>
      </div>
      <Modal
        isOpen={modalIsOpen}
        onRequestClose={() => setModalIsOpen(false)}
        contentLabel="Feedback Modal"
      >
        <h2>Good Work on {currentProblem?.topic}!</h2>
        <p>On a scale of 1-5, how much more practice on {currentProblem?.topic} would you like?</p>
        <div>
          {[1, 2, 3, 4, 5].map(value => (
            <label key={value}>
              <input
                type="radio"
                value={value}
                checked={feedback === value}
                onChange={() => setFeedback(value)}
              />
              {value}
            </label>
          ))}
        </div>
        <button onClick={handleFeedbackSubmit}>Submit</button>
      </Modal>
    </div>
  );
}

export default App;
